                    { "tt0103850", "Tim Robbins", "Documentary-style look at the fictional Senatorial campaign of Bob Roberts, an arch-conservative folk singer turned politician. This political satire includes several original songs co-written and performed by writer/director/star Tim Robbins, and cameo appearances by other stars as reporters and news anchors.", "https://m.media-amazon.com/images/M/MV5BZGY2MWQwZTMtNGY2MS00ZGE2LTk3MjQtNWQwNWY5OGI2NWJlXkEyXkFqcGdeQXVyNzc5MjA3OA@@._V1_SX300.jpg", "04 Sep 1992", "Bob Roberts" },
                    { "tt0108631", "Alister Smart", "In Sydney, Australia, a saxophone playing schoolboy, a tap dancing schoolgirl, a retired actress and 68 y.o. Bob form friendships despite age gap.", "https://m.media-amazon.com/images/M/MV5BMmFjZjZkY2QtNzI2MC00ZTgxLThhN2YtNTllYzNiZDdkODA2XkEyXkFqcGdeQXVyMDM3ODU0Nw@@._V1_SX300.jpg", "N/A", "You and Me and Uncle Bob" },
